import requests,re
import urllib3

class Scraper:

    BASE_URL = "https://play.google.com/"
    proxies = {
        "http": "http://127.0.0.1:8080", 
        "https": "http://127.0.0.1:8080"
    }
    
    package_fetch_regex = r'\/store\/apps\/details\?id=([a-zA-Z0-9.]*)'
    package_fetch_regex_in_pagination_page = r'(=?\/store\/apps\/details\?id\\\\u003d)([a-zA-Z0-9.]*)\\'
    pagination_token_regex = r'107 QbiEs;.*.;108'
    pagination_token_regex_in_pagination_page=r'(=?true,2,null,\[null,\\\")(.*.)(=?\\"\],true\],null,null)'
    
    unique = lambda self,lst: list(dict.fromkeys(lst))


    def __init__(self,use_proxy=True) -> None:

        self.USE_PROXY = use_proxy
        urllib3.disable_warnings(urllib3.exceptions.InsecureRequestWarning)
        
        


    def search_by_keyword(self,keyword):

        """Returns a list with package names as a 
        result of a search query"""

        session = requests.Session()
        package_names = []
        
        if self.USE_PROXY: 
           session.proxies = self.proxies

        resp = session.get(self.BASE_URL + "store/search?q="+keyword+"&c=apps",verify=False)

        for package in re.findall(self.package_fetch_regex,resp.text):

            package_names.append(package)
            pagination_token = re.findall(self.pagination_token_regex,resp.text)
            print(pagination_token)

        if pagination_token:
            token = self.paginate_post(pagination_token[0][10:-4])
            self.paginate(token,session,package_names)

        return self.unique(package_names)
        




    
    def paginate(self,token,session,package_names):
        

        resp = session.post(self.BASE_URL + "_/PlayStoreUi/data/batchexecute",data = {"f.req":token},verify=False)
       
        for package in re.findall(self.package_fetch_regex_in_pagination_page,resp.text):
            package_names.append(package[1])
        
        next_pagination_token = re.search(self.pagination_token_regex_in_pagination_page,resp.text)
        
        if next_pagination_token:
            self.paginate(self.paginate_post(next_pagination_token.group(2)),session,package_names)

           





